/*Data for the table `web_article` */

insert  into `web_article`(`id`,`cid`,`title`,`text`,`type`,`typeName`,`state`,`stateName`,`count`,`createTime`,`operater`,`operaterName`,`image`,`sort`) values (419,'geneon.about','公司简介','<p style=\"text-indent:2em;\">\r\n	<span style=\"line-height:2;color:#003399;\">长春市志昂生物科技有限公司</span><span style=\"line-height:2;\">（简称志昂生物）坐落于吉林省长春市高新区，是一家集研发、生产、销售、服务为一体的专业化生物技术公司。公司前身为长春市博坤生物科技有限公司（简称博坤生物）的生命科学部。2012年5月，博坤生物进行业务拆分，博坤生物专注于公安法检领域，新成立的志昂生物从事公安法检以外的生命科学领域。</span> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	&nbsp;<span style=\"line-height:2;\">公司拥有雄厚的技术力量和科技实力，研发团队中具有博士学位者占50%以上。公司具备独立的研发体系和较强的研发能力，目前已成功研发具有国际先进水平的磁珠法核酸提取试剂盒数十种，包括全血、血浆、植物、动物、昆虫、病毒等核酸提取试剂盒。公司在重视产品质量的同时，结合客户需求，提供个性化的手工及自动化解决方案，以及全方位的技术支持和服务。</span> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	&nbsp;<span style=\"line-height:2;\">志昂生物敢为人先，引领市场，秉承“产品关键，服务至上”的理念，愿与各界同仁携手合作，共创辉煌！</span> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	&nbsp;\r\n</p>','article.state.normal','普通','visible.true','显示',0,'2012-11-26 22:31:55','admin','管理员','',NULL),(454,'geneonhtml.banner','广告位','imgUrl1=\"/geneon/attached/image/20121128/20121128105038_649.jpg\";\r\nimgtext1=\"#\"\r\nimgLink1=escape(\"#\");\r\nimgUrl2=\"/geneon/attached/image/20121128/20121128105101_434.jpg\";\r\nimgtext2=\"#\"\r\nimgLink2=escape(\"#\");\r\nimgUrl3=\"/geneon/attached/image/20121128/20121128105045_671.jpg\";\r\nimgtext3=\"#\"\r\nimgLink3=escape(\"#\");\r\nimgUrl4=\"/geneon/attached/image/20121128/20121128105109_827.jpg\";\r\nimgtext4=\"#\"\r\nimgLink4=escape(\"#\");\r\nimgUrl5=\"/geneon/attached/image/20121128/20121128104955_653.jpg\";\r\nimgtext5=\"#\"\r\nimgLink5=escape(\"#\");\r\n var focus_width=1003;\r\n var focus_height=270;\r\n var text_height=0;\r\n var swf_height = focus_height+text_height;\r\n var pics=imgUrl1+\"|\"+imgUrl2+\"|\"+imgUrl3+\"|\"+imgUrl4+\"|\"+imgUrl5;\r\n var links=imgLink1+\"|\"+imgLink2+\"|\"+imgLink3+\"|\"+imgLink4+\"|\"+imgLink5;\r\n var texts=imgtext1+\"|\"+imgtext2+\"|\"+imgtext3+\"|\"+imgtext4+\"|\"+imgtext5;\r\n','article.state.normal','普通','visible.true','显示',0,'2012-11-27 23:51:50','system','开发维护员','',NULL),(455,'geneonhtml.cntact','推荐解决方案','<div class=\"part\">\r\n<div class=\"parta\">\r\n  <h1>推荐解决方案</h1>\r\n</div>\r\n  <div class=\"partb\">\r\n  	<div class=\"qh\">\r\n  	  <object id=\"FlashID2\" classid=\"clsid:D27CDB6E-AE6D-11cf-96B8-444553540000\" width=\"266\" height=\"223\">\r\n  	    <param name=\"movie\" value=\"flash/bh.swf\" />\r\n  	    <param name=\"quality\" value=\"high\" />\r\n  	    <param name=\"wmode\" value=\"opaque\" />\r\n  	    <param name=\"swfversion\" value=\"6.0.65.0\" />\r\n  	    <!-- 此 param 标签提示使用 Flash Player 6.0 r65 和更高版本的用户下载最新版本的 Flash Player。如果您不想让用户看到该提示，请将其删除。 -->\r\n  	    <param name=\"expressinstall\" value=\"Scripts/expressInstall.swf\" />\r\n  	    <!-- 下一个对象标签用于非 IE 浏览器。所以使用 IECC 将其从 IE 隐藏。 -->\r\n  	    <!--[if !IE]>-->\r\n  	    <object type=\"application/x-shockwave-flash\" data=\"flash/bh.swf\" width=\"266\" height=\"223\">\r\n  	      <!--<![endif]-->\r\n  	      <param name=\"quality\" value=\"high\" />\r\n  	      <param name=\"wmode\" value=\"opaque\" />\r\n  	      <param name=\"swfversion\" value=\"6.0.65.0\" />\r\n  	      <param name=\"expressinstall\" value=\"Scripts/expressInstall.swf\" />\r\n  	      <!-- 浏览器将以下替代内容显示给使用 Flash Player 6.0 和更低版本的用户。 -->\r\n  	      <div>\r\n  	        <h4>此页面上的内容需要较新版本的 Adobe Flash Player。</h4>\r\n  	        <p><a href=\"http://www.adobe.com/go/getflashplayer\"><img src=\"http://www.adobe.com/images/shared/download_buttons/get_flash_player.gif\" alt=\"获取 Adobe Flash Player\" width=\"112\" height=\"33\" /></a></p>\r\n	        </div>\r\n  	      <!--[if !IE]>-->\r\n	      </object>\r\n  	    <!--<![endif]-->\r\n	    </object>\r\n  	</div>\r\n      <div class=\"pc\"><img src=\"images/jqr.jpg\" width=\"266\" height=\"202\" /></div>\r\n      <div class=\"clear\"></div>\r\n  </div>\r\n  <div class=\"clear\"></div>\r\n  </div>','article.state.normal','普通','visible.true','显示',0,'2012-11